import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getItems, deleteItem } from "../api/itemService";

export default function ViewItemsPage() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchItems = async () => {
    try {
      setLoading(true);
      const res = await getItems();
      setItems(res.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchItems();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this book?")) return;
    try {
      await deleteItem(id);
      setItems((prev) => prev.filter((i) => i._id !== id));
    } catch (err) {
      alert("Delete failed");
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Books</h1>
        <Link
          to="/add-item"
          className="px-4 py-2 bg-indigo-600 text-white rounded"
        >
          Add New
        </Link>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {items.map((item) => (
          <div
            key={item._id}
            className="bg-white rounded-lg shadow p-5 hover:shadow-lg transition"
          >
            <h2 className="text-xl font-semibold">{item.title}</h2>
            <p className="text-sm text-gray-700"><b>Author: </b>{item.author}</p>
            <p className="mt-2 text-gray-700"> <b>Description: </b>
              {item.description?.slice(0, 100)}
            </p>

                <p className="mt-2 text-gray-700"> <b>Genre </b>

            </p>

                <div className="flex gap-2 ">
                <Link
                  to={`/items/${item._id}`}
                  className="px-3 py-1 bg-blue-500 text-white rounded"
                >
                  View
                </Link>
                <Link
                  to={`/edit-item/${item._id}`}
                  className="px-3 py-1 bg-yellow-400 rounded"
                >
                  Edit
                </Link>
                <button
                  onClick={() => handleDelete(item._id)}
                  className="px-3 py-1 bg-red-500 text-white rounded"
                >
                  Delete
                </button>
              </div>
          </div>
        ))}
      </div>
    </div>
  );
}
