import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function HomePage() {
  const [count, setCount] = useState(0);

  useEffect(() => {
    axios.get('http://localhost:5000/api/items')
      .then(res => setCount(res.data.length))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500 text-white p-6">
      <h1 className="text-5xl font-bold mb-4 drop-shadow-lg">ðŸ“š Book Management System</h1>
      <p className="text-lg max-w-2xl text-center mb-4">
        We currently have <span className="font-bold">{count}</span> books in our collection!
      </p>
      <div className="flex gap-4">
        <Link to="/items" className="px-6 py-3 bg-yellow-400 text-black font-semibold rounded-lg shadow-lg hover:bg-yellow-300 transform hover:scale-105 transition">
          ðŸ“– View Books
        </Link>
        <Link to="/add-item" className="px-6 py-3 bg-green-400 text-black font-semibold rounded-lg shadow-lg hover:bg-green-300 transform hover:scale-105 transition">
          âž• Add Book
        </Link>
      </div>
    </div>
  );
}
