import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getItem } from '../api/itemService';

export default function ItemDetailsView(){
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(()=>{
    const fetch = async ()=>{ try{ const res = await getItem(id); setItem(res.data); }catch(err){ console.error(err); } setLoading(false); }
    fetch();
  },[id]);



  return (
    <div className="max-w-3xl mx-auto bg-white p-6 rounded shadow">
      <h1 className="text-3xl font-bold">{item.title}</h1>
      <p className="text-gray-600">By {item.author}</p>
      <p className="mt-4">{item.description}</p>
      <p className="mt-2 text-sm text-gray-600">{item.genre} â€¢ ${item.price}</p>
      <p className="mt-2">Stock: {item.stock}</p>
      <div className="mt-4">
        <Link to={`/edit-item/${item._id}`} className="px-4 py-2 bg-yellow-400 rounded mr-2">Edit</Link>
        <Link to="/items" className="px-4 py-2 bg-indigo-600 text-white rounded">Back</Link>
      </div>
    </div>
  );
}
