import React, { useState } from 'react';

export default function ItemFormView({ initial = {}, onSubmit }){
  const [title, setTitle] = useState(initial.title || '');
  const [author, setAuthor] = useState(initial.author || '');
  const [genre, setGenre] = useState(initial.genre || '');
  const [price, setPrice] = useState(initial.price || 0);
  const [stock, setStock] = useState(initial.stock || 0);
  const [description, setDescription] = useState(initial.description || '');

  const handleSubmit = (e) => { e.preventDefault(); if(!title.trim() || !author.trim()) return alert('Title and author required'); if(Number(price) < 0 || Number(stock) < 0) return alert('Price and stock must be >= 0'); onSubmit({ title, author, genre, price: Number(price), stock: Number(stock), description }); }

  return (
    <form onSubmit={handleSubmit} className="max-w-xl bg-white p-6 rounded shadow">
      <label className="block mb-2">Title *</label>
      <input value={title} onChange={e=>setTitle(e.target.value)} className="w-full border p-2 mb-4 rounded" />

      <label className="block mb-2">Author *</label>
      <input value={author} onChange={e=>setAuthor(e.target.value)} className="w-full border p-2 mb-4 rounded" />

      <label className="block mb-2">Genre</label>
      <input value={genre} onChange={e=>setGenre(e.target.value)} className="w-full border p-2 mb-4 rounded" />

      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block mb-2">Price</label>
          <input type="number" value={price} onChange={e=>setPrice(e.target.value)} className="w-full border p-2 mb-4 rounded" />
        </div>
        <div>
          <label className="block mb-2">Stock</label>
          <input type="number" value={stock} onChange={e=>setStock(e.target.value)} className="w-full border p-2 mb-4 rounded" />
        </div>
      </div>

      <label className="block mb-2">Description</label>
      <textarea value={description} onChange={e=>setDescription(e.target.value)} className="w-full border p-2 mb-4 rounded" rows={4} />

      <button className="px-4 py-2 bg-indigo-600 text-white rounded">Save</button>
    </form>
  );
}
